import React, { Component } from "react";
// import PolygonData from "./polygon";
import axios from 'axios';

class WebMapDemo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            polygonData: {}
        }
    }




    componentDidMount(){

        //eslint-disable-next-line
        this.map = new AMap.Map('container', {
            zoom: 4,//地图初始化显示时候的缩放程度
            showLabel: false, //不显示地图文字标记
            center: [124.25, 26.25]//设置地图中心点坐标
        });


        const url = 'test.json'
        const polygonValue = {}
        axios.get(url).then(
            response =>{
                console.log(response.data)
                // this.setState({polygonData: response.data})
                this.polygonValue = response.data
                console.log(this.polygonValue)
                for (var i=0;i<this.polygonValue.features.length;i++){
                    var polygonArr = new Array();//创建多边形坐标数组
                    polygonArr.push(this.polygonValue.features[i].geometry.coordinates[0]);
                    //eslint-disable-next-line
                    var polygon = new AMap.Polygon({
                        path: polygonArr,
                        strokeColor: "#2cffff", //线颜色
                        strokeOpacity: 0.2, //线透明度
                        strokeWeight: 3,    //线宽
                        fillColor: "#52d5fc", //填充色
                        fillOpacity: 0.35 //填充透明度
                    })
                    this.map.add(polygon)
                }
            }
        )



    };




    render() {
        return (
            <div>
                null
            </div>
    )
    }

}

export default WebMapDemo;
