import React, { Component } from "react";
// import {Polygon} from "react-amap";
import PolygonData from "./polygon";

class WebMapDemo extends Component {
    constructor(props) {
        super(props);
        this.getPolygonValue = this.getPolygonValue.bind(this)
        this.state = {
            polygonData: []
        }

        this.path = [
            //eslint-disable-next-line
            new AMap.LngLat(122,40.7571),
            //eslint-disable-next-line
            new AMap.LngLat(122,40.5),
            //eslint-disable-next-line
            new AMap.LngLat(121.5,40.5),
            //eslint-disable-next-line
            new AMap.LngLat(121.5,40.8945),
            //eslint-disable-next-line
            new AMap.LngLat(121.5239,40.888),
            //eslint-disable-next-line
            new AMap.LngLat(121.5421,40.866),
            //eslint-disable-next-line
            new AMap.LngLat(121.5589,40.8413),
            //eslint-disable-next-line
            new AMap.LngLat(121.6147,40.8336),
            //eslint-disable-next-line
            new AMap.LngLat(121.6736,40.8342),
            //eslint-disable-next-line
            new AMap.LngLat(121.7289,40.8378),
            //eslint-disable-next-line
            new AMap.LngLat(121.7514,40.8408),
            //eslint-disable-next-line
            new AMap.LngLat(121.9394,40.7783),
            //eslint-disable-next-line
            new AMap.LngLat(122,40.7571)
        ];
    }

    componentDidMount(){
        //eslint-disable-next-line
        this.map = new AMap.Map('container', {
            zoom: 4,//地图初始化显示时候的缩放程度
            showLabel: false, //不显示地图文字标记
            center: [124.25, 26.25]//设置地图中心点坐标
        });
        //eslint-disable-next-line
        const polygon = new AMap.Polygon({
            path: this.path,
            fillColor: '#fff', // 多边形填充颜色
            borderWeight: 2, // 线条宽度，默认为 1
            strokeColor: 'black', // 线条颜色
        });
        this.map.add(polygon);
     }


    getPolygonValue(val) {
        this.setState({
            polygonData: val
        })
        console.log(this.state.polygonData)

    }
    render() {
        return (
            <div>
                <PolygonData getPolygonValue = {this.getPolygonValue}/>
            </div>
    )
    }

}

export default WebMapDemo;
